import React, { useCallback, useState } from 'react';
import logo from '../images/Logo.svg';

const Nav = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = useCallback(() => {
    setIsOpen(prevState => !prevState);
  }, []);

  const closeMenu = useCallback(() => {
    setIsOpen(false);
  }, []);

  return (
    <nav className={`navbar ${isOpen ? 'show-menu' : ''}`}>
      <a href="/">
        <img src={logo} alt="logo" />
      </a>

      <ul
        className={`nav-menu ${isOpen ? 'active' : ''}`}
        style={{
          position: isOpen ? 'absolute' : 'static', // Set absolute positioning on open
          top: isOpen ? '100%' : 'unset', // Position below burger on open (adjust top value as needed)
        }}
      >
        {['home', 'about', 'menu', 'reservations', 'order-online', 'login'].map(page => (
          <li key={page}>
            <a href={`/#${page}`} className="nav-link" onClick={closeMenu}>
              {page.toUpperCase()}
            </a>
          </li>
        ))}
      </ul>

      <div className={`burger ${isOpen ? 'open' : ''}`} onClick={toggleMenu}>
        {Array.from({ length: 3 }, (_, i) => (
          <div key={i} className={`bar${i + 1} ${isOpen ? 'open' : ''}`}></div>
        ))}
      </div>
    </nav>
  );
};

export default Nav;
