import React, { useState } from 'react';

const ReservationForm = () => {
    const [seatingType, setSeatingType] = useState('');
    const [date, setDate] = useState('');
    const [diners, setDiners] = useState(1);
    const [occasion, setOccasion] = useState('');
    const [time, setTime] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
      // Perform reservation logic here
      // You can send the gathered data to a server for processing or display a confirmation message
      console.log('Reservation details:');
      console.log(`Seating type: ${seatingType}`);
      console.log(`Date: ${date}`);
      console.log(`Number of diners: ${diners}`);
      console.log(`Occasion: ${occasion}`);
      console.log(`Time: ${time}`);
    };

    return (
      <div className="reservations">
        <h2 className="reservations__heading">Reservations</h2>
        <form onSubmit={handleSubmit} className="reservations__form">
          <div className="reservations__seating-type">
            <label className="reservations__label">
              <input
                type="radio"
                name="seating"
                value="indoor"
                checked={seatingType === 'indoor'}
                onChange={() => setSeatingType('indoor')}
                className="reservations__input"
              />
              Indoor seating
            </label>
            <label className="reservations__label">
              <input
                type="radio"
                name="seating"
                value="outdoor"
                checked={seatingType === 'outdoor'}
                onChange={() => setSeatingType('outdoor')}
                className="reservations__input"
              />
              Outdoor seating
            </label>
          </div>
          <div className="reservations__field">
            <label className="reservations__label">Date:</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              className="reservations__input"
            />
          </div>
          <div className="reservations__field">
            <label className="reservations__label">Number of Diners:</label>
            <input
              type="number"
              min="1"
              value={diners}
              onChange={(e) => setDiners(parseInt(e.target.value, 10))}
              className="reservations__input"
            />
          </div>
          <div className="reservations__field">
            <label className="reservations__label">Occasion:</label>
            <select
              value={occasion}
              onChange={(e) => setOccasion(e.target.value)}
              className="reservations__input"
            >
              <option value="">Select occasion</option>
              <option value="birthday">Birthday</option>
              <option value="anniversary">Anniversary</option>
              <option value="other">Other</option>
            </select>
          </div>
          <div className="reservations__field">
            <label className="reservations__label">Time:</label>
            <input
              type="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              className="reservations__input"
            />
          </div>
          <button type="submit" className="reservations__button">
            Reserve a Table
          </button>
        </form>
      </div>
    );
  };

  export default ReservationForm;
