import React, { useEffect, useRef } from "react";
import logo from '../images/Logo.svg'

const Nav = () => {
    const headerRef = useRef(null);

    useEffect(() => {
      let prevScrollPos = window.scrollY;

      const handleScroll = () => {
        const currentScrollPos = window.scrollY; 
        const headerElement = headerRef.current; 
        if (!headerElement) { 
          return; 
        } 
        if (prevScrollPos > currentScrollPos) { 
          headerElement.style.transform = "translateY(0)"; 
        } else { 
          headerElement.style.transform = "translateY(-200px)"; 
        } 
        prevScrollPos = currentScrollPos; 
      } 
      window.addEventListener('scroll', handleScroll) 

      return () => { 
        window.removeEventListener('scroll', handleScroll) 
      }
    }, []);

    const handleClick = (anchor) => () => {
      const id = `${anchor}-section`;
      const element = document.getElementById(id);
      if (element) {
        element.scrollIntoView({
          behavior: "smooth",
          block: "start",
        });
      }}


    return (
        <nav>
            <a href='/'>
                <img src={logo} alt='logo'/>
            </a>
            <nav className='burger'>
                <div className='bar1'></div>
                <div className='bar1'></div>
                <div className='bar1'></div>
            </nav>
            <nav>
                <ul>
                    <li>
                    <a href='/#home' onClick={handleClick("home")}>Home</a>
                    </li>
                    <li>

                    </li>
                    <li>

                    </li>
                    <li>

                    </li>
                    <li>

                    </li>
                    <li>

                    </li>
                </ul>

                <a href='/#menu' onClick={handleClick("menu")}>Menu</a>
                <a href='/#reservations' onClick={handleClick("reservations")}>Reservations</a>
                <a href='/#order-online' onClick={handleClick("online-order")}>Order Online</a>
                <a href='/#login' onClick={handleClick("login")}>Login</a>
            </nav>
        </nav>

    );
};

export default Nav;
