                <ul>
                    <li>
                    <a href='/#home' onClick={handleClick("home")}>Home</a>
                    </li>
                    <li>
                    <a href='/#about' onClick={handleClick("about")}>About</a>
                    </li>
                    <li>
                    <a href='/#menu' onClick={handleClick("menu")}>Menu</a>
                    </li>
                    <li>
                    <a href='/#reservations' onClick={handleClick("reservations")}>Reservations</a>
                    </li>
                    <li>
                    <a href='/#order-online' onClick={handleClick("online-order")}>Order Online</a>
                    </li>
                    <li>
