import React, { useState } from 'react';

const BookingForm = () => {
    const [seatingType, setSeatingType] = useState('');
    const [date, setDate]= useState("")
    const [diners, setDiners]= useState(1)
    const [occasion, setOccasion]= useState("")
    const [time, setTime]= useState("")

    const timeOptions = [
        '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM',
        '12:00 PM', '12:30 PM', '1:00 PM', '1:30 PM', '2:00 PM', '2:30 PM',
        '3:00 PM', '3:30 PM', '4:00 PM', '4:30 PM', '5:00 PM', '5:30 PM',
        '6:00 PM', '6:30 PM', '7:00 PM', '7:30 PM', '8:00 PM', '8:30 PM',
        '9:00 PM', '9:30 PM', '10:00 PM'
      ];

    const dinersOptions = Array.from({ length: 10 }, (_, i) => i + 1);

    const handlSubmit= (e) => {

    }
    return (
        <form onSubmit={handlSubmit}>
            <h1>Reservations</h1>
            <fieldset>
                <label>
                    <input
                        type='radio'
                        value='indoor'
                        name='seating'
                        onClick={(e) => setSeatingType(e.target.value)}
                    />
                    Indoor Seating
                </label>
                <label>
                    <input
                        type='radio'
                        value='outdoor'
                        name='seating'
                        onClick={(e) => setSeatingType(e.target.value)}
                    />
                    Outdoor Seating
                </label>
            </fieldset>
            <fieldset>
                <div>
                    <label>Date</label>
                    <input
                        type='date'
                        value={date}
                        onClick={(e) => setDate(e.target.value)}
                        required
                    >
                    </input>
                </div>
                <div>
                    <label>Number of Diners</label>
                    <input
                        type='number'
                        min='1'
                        max='10'
                        value={diners}
                        onClick={(e) => setDiners(e.target.value, 10)}
                        required
                    >
                </input>
                </div>
            </fieldset>
            <fieldset>
                <div>
                    <label>Occasion</label>

                        <select
                            value={occasion}
                            onChange={(e) => setOccasion(e.target.value)}
                            className="reservations__input"
                        >
                            <option value="">Select occasion</option>
                            <option value="birthday">Birthday</option>
                            <option value="anniversary">Anniversary</option>
                            <option value="other">Other</option>
                        </select>
                </div>
                <div>
                    <label>Time</label>
                        <select
                            value={time}
                            onChange={(e) => setTime(e.target.value)}
                        >
                            <option value="">Select time</option>
                            {timeOptions.map((option) => (
                            <option key={option} value={option}>{option}</option>
                            ))}
                        </select>
                    </div>
            </fieldset>
            <button type="submit">Reserve a Table</button>
        </form>
    );
};

export default BookingForm;
