import React, { useState } from 'react';

const BookingForm = () => {
  const [seatingType, setSeatingType] = useState('');
  const [date, setDate] = useState("");
  const [diners, setDiners] = useState(0);
  const [occasion, setOccasion] = useState("");
  const [time, setTime] = useState("");

  const Book = ({ setCurrentComponent }) => {
    const handleReserveClick = () => {
      setCurrentComponent('Booking');
    };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Reservation details:');
    console.log(`Seating type: ${seatingType}`);
    console.log(`Date: ${date}`);
    console.log(`Number of diners: ${diners}`);
    console.log(`Occasion: ${occasion}`);
    console.log(`Time: ${time}`);
    const formData = {
        firstName,
        lastName,
        email,
        phone,
        date,
        diners,
        time,
        occasion,
        seatingType,
        specialRequest,
      };


  };

  const timeOptions = [
    '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM',
    '12:00 PM', '12:30 PM', '1:00 PM', '1:30 PM', '2:00 PM', '2:30 PM',
    '3:00 PM', '3:30 PM', '4:00 PM', '4:30 PM', '5:00 PM', '5:30 PM',
    '6:00 PM', '6:30 PM', '7:00 PM', '7:30 PM', '8:00 PM', '8:30 PM',
    '9:00 PM', '9:30 PM', '10:00 PM'
  ];

  const dinersOptions = Array.from({ length: 10 }, (_, i) => i + 1);

  return (
    <form onSubmit={handleSubmit} className="bookingForm">
      <h1 className="formTitle">Reservations</h1>
      <fieldset className="seatingFieldset">
        <label className="seatingLabel">
          <input
            type='radio'
            value='indoor'
            name='seating'
            checked={seatingType === 'indoor'}
            onChange={(e) => setSeatingType(e.target.value)}
            className="seatingInput"
          />
          Indoor Seating
        </label>
        <label className="seatingLabel">
          <input
            type='radio'
            value='outdoor'
            name='seating'
            checked={seatingType === 'outdoor'}
            onChange={(e) => setSeatingType(e.target.value)}
            className="seatingInput"
          />
          Outdoor Seating
        </label>
      </fieldset>
      <fieldset className="dateAndDinersFieldset">
        <div className="dateDiv">
          <label className="dateLabel">Date</label>
          <input
            type='date'
            value={date || ""}
            onChange={(e) => setDate(e.target.value)}
            placeholder="Select Date"
            required
            className="dateInput"
          />
        </div>
        <div className="dinersDiv">
          <label className="dinersLabel">Number of Diners</label>
          <select
            value={diners || ""}
            onChange={(e) => setDiners(e.target.value)}
            className="dinersSelect"
          >
            <option value="">Select Number of Diners</option>
            {dinersOptions.map((option) => (
              <option key={option} value={option}>{option} diner{option > 1 ? 's' : ''}</option>
            ))}
          </select>
        </div>
      </fieldset>
      <fieldset className="occasionAndTimeFieldset">
        <div className="occasionDiv">
          <label className="occasionLabel">Occasion</label>
          <select
            value={occasion || ""}
            onChange={(e) => setOccasion(e.target.value)}
            className="occasionSelect"
          >
            <option value="">Select Occasion</option>
            <option value="birthday">Birthday</option>
            <option value="anniversary">Anniversary</option>
            <option value="other">Other</option>
          </select>
        </div>
        <div className="timeDiv">
          <label className="timeLabel">Time</label>
          <select
            value={time || ""}
            onChange={(e) => setTime(e.target.value)}
            className="timeSelect"
          >
            <option value="">Select Time</option>
            {timeOptions.map((option) => (
              <option key={option} value={option}>{option}</option>
            ))}
          </select>
        </div>
      </fieldset>
      <button type="submit" className="submitButton"
      onClick={handleReserveClick}
      >Reserve a Table</button>
    </form>
  );
};
}

export default BookingForm;
